import { useState } from "react";

const Note = ({ title, description }) => {
    const [showDescription, setShowDescription] = useState(false);

    return (
        <div className="bg-white shadow-lg rounded-lg p-6 mb-4">
            <h2
                onClick={() => setShowDescription((prevState) => !prevState)}
                className="text-2xl font-bold cursor-pointer hover:text-blue-600 transition duration-300"
            >
                {title}
            </h2>
            {showDescription && (
                <div className="mt-4">
                    <p className="text-gray-600 mb-4">{description}</p>
                    <div className="flex space-x-4">
                        <button className="px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 transition duration-300">
                            Edit Note
                        </button>
                        <button className="px-4 py-2 bg-red-500 text-white rounded-lg hover:bg-red-600 transition duration-300">
                            Delete Note
                        </button>
                    </div>
                </div>
            )}
        </div>
    );
};

export default Note;
