import React, { useState } from 'react'
import Note from '../Note/note'
import { NOTES } from './data'
import note from '../Note/note'

const NotesList = () => {
  const [currentNotes, setCrurentNotes] = useState(NOTES)
  const deleteNote = (id) => {
    setCrurentNotes(currentNotes.filter((note) => note.id !== id))
  }

  const addNote = (title, description) => {
    setCrurentNotes([
      ...currentNotes,
      { id: Math.random(), title, description },
    ])
  }

  return (
    <div className="bg-amber-500 p-10 rounded-xl flex flex-col gap-4 scale-125">
      <h1 className="mb-6 text-center text-5xl font-bold">Notatki</h1>
      <hr />
      <div>
        {currentNotes.map((currentNote) => (
          <Note
            key={currentNote.id}
            title={currentNote.title}
            description={currentNote.description}
            deleteNote={() => deleteNote(currentNote.id)}
          />
        ))}
      </div>
      <hr />
      <button
        type="button"
        className="rounded-full bg-white px-4 py-2 text-amber-500 transition duration-300 hover:bg-red-600"
      >
        {' '}
        Add Noet
      </button>
    </div>
  )
}

export default NotesList
