import React, { useState } from 'react'
import Note from '../Note/note'
import { NOTES } from './data'
import Modal from '../Modal/modal'

const NotesList = () => {
  const [currentNotes, setCurentNotes] = useState(NOTES)
  const [isModal, setIsModal] = useState(false)
  const [newTitle, setNewTitle] = useState('')
  const [newDescription, setNewDescription] = useState('')
  const [currentDescription, setCurrentDescription] = useState('')

  const deleteNote = (id) => {
    setCurentNotes(currentNotes.filter((note) => note.id !== id))
  }

  const addNote = () => {
    if (newTitle.trim() && newDescription.trim()) {
      setCurentNotes([
        ...currentNotes,
        { id: Math.random(), title: newTitle, description: newDescription },
      ])
      setNewTitle('')        // Clear the title input field
      setNewDescription('')   // Clear the description input field
      setIsModal(false)       // Close the modal
    }
  }

  return (
    <div className="bg-amber-500 p-10 rounded-xl flex flex-col gap-4 scale-125">
      <h1 className="mb-6 text-center text-5xl font-bold">Notatki</h1>
      <hr />
      <div>
        {currentNotes.map((currentNote) => (
          <Note
            key={currentNote.id}
            title={currentNote.title}
            description={currentNote.description}
            deleteNote={() => deleteNote(currentNote.id)}
          />
        ))}
      </div>
      <hr />

      <button
        type="button"
        className="rounded-full bg-white px-4 py-2 text-amber-500 transition duration-300 hover:bg-black hover:text-white"
      >
        Add Noete
      </button>
      <Modal active={isModal} setIsModal={setIsModal} addNote={addNote}>
        <h2 className="ftex-2xl font-bold nb-8">Add Noete</h2>
        <form className="flex flex-col gap-2">
          <label htmlFor="title">Title: </label>
          <input
            id="title"
            name="title"
            type={'text'}
            onChange={(event) => addNote(event.target.value)}
          />
          <label htmlFor="description">Description</label>
          <input
            id="description"
            name="description"
            type={'text'}
            value={currentDescription}
            onChange={(event) => addNote(...Note, event.target.value)}
          />
          <button
            type={'button'}
            className="py-2 px-4 bg-white rounded hover:text-indigo-700 transition"
            onClick={() => setIsModal(false)}
          >
            Submit
          </button>
        </form>
      </Modal>
    </div>
  )
}

export default NotesList
