import { useState } from "react";
import Note from "./note.js";

const Notelist = () => {
  const [notes, setNotes] = useState([
    { id: 1, title: "Note 1", description: "This is the description for note 1" },
    { id: 2, title: "Note 2", description: "This is the description for note 2" },
  ]);

  const handleEdit = (id) => {
    const newDescription = prompt("Enter new description:");
    if (newDescription) {
      setNotes((prevNotes) =>
          prevNotes.map((note) =>
              note.id === id ? { ...note, description: newDescription } : note
          )
      );
    }
  };

  const handleDelete = (id) => {
    setNotes((prevNotes) => prevNotes.filter((note) => note.id !== id));
  };

  return (
      <div>
        {notes.map((note) => (
            <div key={note.id} className="mb-4">
              <Note
                  title={note.title}
                  description={note.description}
              />
              <div className="mt-2">
                <button
                    onClick={() => handleEdit(note.id)}
                    className="bg-blue-500 text-white rounded-full px-4 py-2 mr-2 hover:bg-blue-600"
                >
                  Edit Note
                </button>
                <button
                    onClick={() => handleDelete(note.id)}
                    className="bg-red-500 text-white rounded-full px-4 py-2 hover:bg-red-600"
                >
                  Delete Note
                </button>
              </div>
            </div>
        ))}
      </div>
  );
};

export default Notelist;
