import React, { useState } from "react";

const Note = ({ title, description, onDelete, onEdit }) => {
    const [isExpanded, setIsExpanded] = useState(false);

    const toggleContent = () => {
        setIsExpanded(!isExpanded);
    };

    const editNote = () => {
        const newTitle = prompt("Enter new title", title);
        const newDescription = prompt("Enter new description", description);
        if (newTitle) setTitle(newTitle);
        if (newDescription) setDescription(newDescription);
        toggleContent();
    };

    const deleteNote = () => {
        prompt("Are you sure you want to delete this note?", "Yes");
        setTitle("Deleted Note");
        setDescription("");

    };


    return (
        <div className="bg-white shadow-lg rounded-lg p-4 m-4 transition transform hover:scale-105 hover:shadow-xl">
            <h2
                onClick={toggleContent}
                className="text-2xl font-semibold cursor-pointer text-gray-800 mb-2"
            >
                {title}
            </h2>
            {isExpanded && (
                <div>
                    <p className="text-gray-600 mb-4">{description}</p>
                    <div className="flex space-x-2">
                        <button
                            className="bg-blue-500 text-white px-4 py-2 rounded-full hover:bg-blue-600 transition duration-300"
                            onClick={onEdit}>Edit Note</button>
                        <button
                            className="bg-red-500 text-white px-4 py-2 rounded-full hover:bg-red-600 transition duration-300"
                            onClick={onDelete}>Delete Note</button>
                    </div>
                </div>
            )}
        </div>
    );
};

export default Note;
